<span class="line"><span style="color:#A6ACCD;">console</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">log</span><span style="color:#A6ACCD;">(</span><span style="color:#82AAFF;">Number</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">123 456</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">))    </span><span style="color:#676E95;font-style:italic;">// =&gt; NaN</span></span>
